mapper.update:
UPDATE BP_server 
SET name=#{name}, ip=#{ip}, os=#{os}, loc=#{loc}, 
	mac=#{mac}, control_num=#{control_num}, dsc=#{dsc}
WHERE server_no=#{server_no}

mapper.select:
SELECT BM.sc_no
FROM BP_Mgmt_server BM
WHERE server_no = #{server_no}

 		SELECT BM.server_no
		FROM BP_Mgmt_server BM
		WHERE sc_no = #{sc_no}

select count(bps.*)
from bp_server bps
where mac=#{mac}
and server_no != #{server_no}
