mapper.select:
select bn.nc_no, bn.nc_title, bn.nc_content, bn.nc_readCount, bn.nc_writeDate, bu.user_id, bu.user_no, bu.name from bp_notification bn, bp_user bu where bn.user_no = bu.user_no
AND nc_title iLIKE '%'||#{searchWord}||'%' OR nc_content iLIKE '%'||#{searchWord}||'%'
ORDER BY bn.nc_no desc LIMIT #{rows} OFFSET (#{page} -1) * #{rows}

mapper.update:
UPDATE bp_notification
SET nc_title=#{nc_title}, nc_content=#{nc_content}
WHERE nc_no=#{nc_no}

UPDATE bp_notification
SET nc_readCount=nc_readCount+1
WHERE nc_no=#{nc_no}

mapper.select:
select file_no, name, nc_no, fileName, uploadedFileName, fileSize, contentType, downlink
from bp_file
where nc_no = #{nc_no}

select file_no, name, nc_no, fileName, uploadedFileName, fileSize, contentType, downlink
from bp_file
where file_no = #{file_no}
